import { Component, OnInit } from '@angular/core';
import { Product } from './models/product.model';
import { ProductService } from './services/product.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'ShopVerse Product Dashboard';

  products: Product[] = [];
  selectedProduct: Product | null = null;
  editingProduct: Product | null = null;

  // Controls which screen we see
  viewMode: 'list' | 'detail' = 'list';

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.loadProducts();
  }

  private loadProducts(): void {
    this.products = this.productService.getProducts();
  }

  private showListView(): void {
    this.viewMode = 'list';
    this.selectedProduct = null;
    this.editingProduct = null;
  }

  private showDetailViewFor(product: Product | null): void {
    this.viewMode = 'detail';
    this.selectedProduct = product;
    this.editingProduct = product;
  }

  // When user clicks a product in the list
  handleProductSelected(product: Product): void {
    this.showDetailViewFor(product);
  }

  // When user clicks "Add New Product" in the header
  handleCreateNew(): void {
    this.viewMode = 'detail';
    this.selectedProduct = null;   // no product selected -> create mode
    this.editingProduct = null;
  }

  // When the form is submitted (create or update)
  handleProductSaved(product: Product): void {
    if (product.id) {
      this.productService.updateProduct(product);
    } else {
      this.productService.addProduct(product);
    }
    this.loadProducts();
    this.showListView(); // go back to all products after save
  }

  // When the user cancels from the form
  handleFormCancelled(): void {
    this.showListView();
  }
}
<div class="app-shell">
  <header class="app-header">
    <div>
      <h1>ShopVerse – Product Dashboard</h1>
      <span>Internal catalog management for product managers</span>
    </div>
    <button class="btn btn-secondary btn-sm" (click)="handleCreateNew()">
      + Add New Product
    </button>
  </header>

  <main class="app-main">
    <div class="app-layout">
      <section class="card">
        <div class="card-header">
          <div>
            <div class="card-title">Product Catalog</div>
            <div class="section-subtitle">Search, filter and select products</div>
          </div>
        </div>
        <app-product-list
          [products]="products"
          (selectProduct)="handleProductSelected($event)">
        </app-product-list>
      </section>

      <section class="card">
        <div class="card-header">
          <div>
            <div class="card-title">Details &amp; Edit</div>
            <div class="section-subtitle">
              View and update product information
            </div>
          </div>
        </div>

        <ng-container *ngIf="selectedProduct; else noSelection">
          <app-product-detail
            [product]="selectedProduct"
            (clearSelection)="handleDetailClear()">
          </app-product-detail>
        </ng-container>

        <ng-template #noSelection>
          <p class="section-subtitle" style="margin-bottom: 0.75rem;">
            Select a product from the list or click “Add New Product” to create one.
          </p>
        </ng-template>

        <app-product-form
          [product]="editingProduct"
          (save)="handleProductSaved($event)"
          (cancel)="handleFormCancelled()">
        </app-product-form>
      </section>
    </div>
  </main>
</div>
